import React, { Fragment, useState, useContext } from "react";
import proyectoContext from "../../context/proyectos/proyectoContext";

const NuevoProyecto = () => {
  //obtener el state del formulario
  const proyectosContext = useContext(proyectoContext);
  const {
    formulario,
    errorformulario,
    mostrarFormulario,
    agregarProyecto,
    mostrarError,
  } = proyectosContext;

  //state para proyecto
  const [programas, guardarProgramas] = useState({});
  const [proyecto, guardarProyecto] = useState({
    nombre: "",
  });

  //Extraer nombre de proyecto
  const { nombre } = proyecto;

  //lee los contenidos del input
  const onChangeProyecto = (e) => {
    guardarProyecto({
      ...proyecto,
      [e.target.name]: e.target.value,
    });
  };

  //cuando el usuario envia el proyecto
  const onSubmitProyecto = (e) => {
    e.preventDefault();

    //validar el proyecto
    if (nombre === "") {
      mostrarError();
      return;
    }
    //agregar el state
    agregarProyecto(proyecto);
    //reinicar el form
    guardarProyecto({
      nombre: "",
    });
  };
  //mostar formulario
  const onClickFormulario = () => {
    //mostrarFormulario();
    const resultado = fetch("http://erpdv.preupdv.cl:8090/v1/carro/programas");
    const programas = resultado.then((respuesta) => respuesta.json());
    programas
      .then((resultado) => {
        console.log(resultado);
        
      })
      .catch((e) => console.log(e));
  };

  const onClickProducto = () => {
    // mostrarProductos();
  };
  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-block btn-primario"
        onClick={onClickFormulario}
      >
        Ver Programas
      </button>

      {errorformulario ? (
        <p className="mensaje error">El Nombre del Cliente es obligatorio</p>
      ) : null}
    </Fragment>
  );
};

export default NuevoProyecto;
